def find_content(url,prev_list):
    article = Article(url, keep_article_html=True, fetch_images=False)
    article.download()
    article.parse()
    content = article.text
    if content.count(' ') > 100 and article.title not in prev_list:
        print(article.title)
        return ['Done',post_content(article,url)]
    else:
        return 'Not Done'

def post_content(article,source):
    html = article.article_html
    #print(html)
    soup = BeautifulSoup(html, 'html.parser')
    #print(soup)
    for img_tag in soup.find_all('img'):
        img_tag.decompose()
    #print(soup)
    for a_tag in soup.find_all('a'):
        a_tag.decompose()
    #print(soup)
    unwanted_classes = ['about-the-author', 'author-news','newsletter-section','inner-subscription']
    for class_name in unwanted_classes:
        for element in soup.find_all('p', class_=class_name):
            element.extract()
    #print(soup)
    for tag in soup.find_all(True):
        for attr in list(tag.attrs):
            del tag[attr]
    #print(soup)
    content = str(soup)
    #print(content)
    content = content.replace('<div>','')
    #print(content)
    content = content.replace('</div>','')
    # print(content)
    content = content.replace('<p></p>','')
    content = content.replace('<br/>','<br>')
    #print(content)
    article.nlp()
    category = source.split('/')[3].capitalize()
    content = content[:content.find('SHARE THIS ARTICLE ON')-3]
    #print(content.find('SHARE THIS ARTICLE ON'))
    context = {
    'title' : article.title,
    'image' : article.top_image,
    'content' : content,
    'summary' : article.summary,
    'tags' : list(article.meta_data['keywords'].split(',')),
    'source': source,
    'category' : category}
    #print(context)
    return context

